void K0_PutVal(bool Val)
{
  if (Val) {
    setReg8Bits(PORTK, 1);             /* BIT0=1 */
    Shadow_K |= (byte)1;               /* Set-up shadow variable */
  } else { /* !Val */
    clrReg8Bits(PORTK, 1);             /* BIT0=0 */
    Shadow_K &= (byte)~1;              /* Set-up shadow variable */
  } /* !Val */
}

